import React, { useContext } from "react";
import {
  BoldLink,
  BoxContainer,
  FormContainer,
  Input,
  MutedLink,
  SubmitButton,
} from "./common";
import { Marginer } from "../marginer";
import { AccountContext } from "./accountContext";
import { useState } from "react";
import { createApiEndpoint } from "../../api";

// export function SignupForm(props) {
//   const { switchToSignin } = useContext(AccountContext);
//   const [FirstName, setFirstName] = useState(" ");
//   const [LastName, setLastName] = useState(" ");
//   const [Password, setPassword] = useState(" ");
//   const [email, setEmail] = useState(" ");
//   const [role, setRole] = useState(" ");


//   const mySignUp = () => {createApiEndpoint("Register").create({Id:3, FirstName:FirstName, lastName:LastName, Email:email, Password:Password, Role:role}).then(res => console.log(res)).catch(err => console.log(err) )}

function Regster(props) {  
  const [data, setdata] = useState({ Email: '', Password: '', FirstName: '', LastName: '',Role: '' })  
  const apiUrl = "http://localhost:12107/api/Register";  
  const Registration = (e) => {  
    e.preventDefault();  
    debugger;  
    const data1 = { Email: data.Email, Password: data.Password, FirstName: data.FirstName, LastName: data.LastName, Role: data.Role };  
    axios.post(apiUrl, data1)  
      .then((result) => {  
        debugger;  
        console.log(result.data);  
        if (result.data.Status == 'Invalid')  
          alert('Invalid User');  
        else  
          props.history.push('/Dashboard')  
      })  
  }  
  const onChange = (e) => {  
    e.persist();  
    debugger;  
    setdata({ ...data, [e.target.name]: e.target.value });  
  }  
  return (
    <BoxContainer>
      <FormContainer>
        <Input onChange ={onChange} value={data.FirstName} type="text" placeholder="First Name" />
        <Marginer direction="vertical" margin="0.6em" />
        <Input onChange ={onChange} value={data.LastName} type="text" placeholder="Last Name" />
        <Marginer direction="vertical" margin="0.6em" />
        <Input onChange ={onChange} value={data.Email} type="email" placeholder="Email" />
        <Marginer direction="vertical" margin="0.6em" />
        <Input onChange ={onChange} value={data.Password} type="password" placeholder="Password" />
        <Marginer direction="vertical" margin="0.6em" />
        <Input onChange ={onChange} value={data.Role} type="text" placeholder="Role" />
        <Marginer direction="vertical" margin="0.6em" />
      </FormContainer>
      <Marginer direction="vertical" margin={10} />
      <SubmitButton type="submit">Signup</SubmitButton>
      <Marginer direction="vertical" margin="1em" />
      <MutedLink href="#">
        Already have an account?
        <BoldLink href="#" onClick={switchToSignin}>
          Signin
        </BoldLink>
      </MutedLink>
    </BoxContainer>
  );
}
export default Regster
