import React from 'react';
import Header from '../header/header';
import Box from '@mui/material/Box';
import {
    FormContainer,
    SubmitButton
  } from "./common";
import { Marginer } from "../marginer";
import styles from './keyPage.module.css';
import { useState } from "react";
import { createApiEndpoint, ENDPOINTS } from "../../api";
 
export default function Keypage() {
    const [KeyID, setDisplayID] = useState([]);
    const [KeyDta, setDisplayKey] = useState([]);
    const [Value, setDisplayValue] = useState([]);
    const mydata = {SubDirectory:'FileStorage'};

    const callDisplayKey = () => {createApiEndpoint(ENDPOINTS.TRANSLATION).fetchAll(mydata).
        then(myresult => {console.log(myresult)
          //allKeys = myresult.data.keys;
          console.log(myresult.data.KeyID);
          setDisplayKey(myresult.data.Key); 
          setDisplayID(myresult.data.KeyID)
          setDisplayValue(myresult.data.Value)
          }).
        catch(
          err => {
            console.log(err);
          }
         )}

    const [key, setKey] = useState('');
    const [value, setValue] = useState('');
    const data = {Key:key, Value:value};
  const saveKey = () => {createApiEndpoint(ENDPOINTS.TRANSLATION).create(data).
  then(result => {console.log(result)
    console.log(result.data);
    }).
  catch(
    err => {
      console.log(err);
    }
   )
  }

    return (
        <div className = {styles.main}>
            <Header/>
            <div className={styles.topContainer}>
                <div className= {styles.midContainer}>
                    <span className={styles.left}>

                    </span>
 
                    <span className={styles.right}>
                        <div className={styles.rightTop}>
                        <SubmitButton onClick={callDisplayKey} type="submit">Show Data</SubmitButton>
                        <ol>
                           {mykeys.map((reptile) => (
                           <li> {reptile}</li>
                        ))}
                        </ol>
                        </div>
 
                        <div className={styles.rightbottom}>
                        <FormContainer>
                        <select className= {styles.form1} id = "dropdown">
                                    <option value="en">en</option>
                                    <option value="ur">ur</option>
                        </select>
                        <input className = {styles.textBox} onChange ={t => setKey(t.target.value)} type="text" placeholder="Key" />
                        <Marginer direction="vertical" margin="0.6em" />
                        <input className = {styles.textBox} onChange ={t => setValue(t.target.value)} type="text" placeholder="Value" />
                        <Marginer direction="vertical" margin="1em" />
                        <SubmitButton onClick={saveKey} type="submit">Save</SubmitButton>
                        <Marginer direction="vertical" margin="1em" />
                        </FormContainer>
                        
                        </div>
                    </span>
                </div>
            </div>
        </div>
    )
}
