import React, { useContext } from "react";
import { Link } from "react-router-dom";
import {
  BoldLink,
  BoxContainer,
  FormContainer,
  Input,
  MutedLink,
  SubmitButton,
} from "./common";
import { Marginer } from "../marginer";
import { AccountContext } from "./accountContext";
import { useState } from "react";
import { createApiEndpoint, ENDPOINTS } from "../../api";
import { useNavigate } from "react-router-dom";

export function LoginForm(props) {
  const { switchToSignup } = useContext(AccountContext);
  const [userName, setUserName] = useState('');
  const [password, setPassword] = useState('');

  const data = {UserName:userName, Password:password};
  const myLogin = () => {createApiEndpoint(ENDPOINTS.LOGIN).create(data).
  then(result => {console.log(result)
    console.log(result.data);   
    console.log(result.status);  
       
    // else if(result.status == 400) {
    //   alert('Invalid User'); 
    // }
    
    }).
  catch(err => console.log(err)
   )
   if (result.status == 200) {
    const navigate = useNavigate();
    navigate('/home');
     //props.history.push('/home');  
     //return <Redirect to="/home" />
  }  }
  
   

  return(
    <BoxContainer>
      <FormContainer>
        <Input onChange ={t => setUserName(t.target.value)} type="text" placeholder="UserName" />
        <Marginer direction="vertical" margin="0.6em" />
        <Input onChange ={t => setPassword(t.target.value)} type="password" placeholder="Password" />
      </FormContainer>
      <Marginer direction="vertical" margin={10} />
      <BoldLink href="#">Forget your password?</BoldLink>
      <Marginer direction="vertical" margin="1.6em" />
      <SubmitButton type="submit">Signin</SubmitButton>
      <Marginer direction="vertical" margin="1em" />
      <MutedLink href="#">
        Don't have an account?{" "}
        <BoldLink href="#" onClick={switchToSignup}>
          Signup
        </BoldLink>
      </MutedLink>
    </BoxContainer>
  );
}
