import React from 'react'
import Header from '../header/header';
import styles from './SetupLanguage.module.css';
import {Link} from 'react-router-dom';
import { useState } from "react";
import { createApiEndpoint, ENDPOINTS } from "../../api";
 
 
export default function SetupLanguage() {

    //const {switchToSignin } = useContext(AccountContext);
    const [sourceLanguage, setSourceLanguage] = useState('');
    const [targetLanguage, setTargetLanguage] = useState('');
    const [sName, setsName] = useState('');
    const [tName, settName] = useState('');
  
    const data = {SourceLanguage:sourceLanguage, TargetLanguage:targetLanguage, SourceLanguageName:sName, TargetLanguageName:tName};
    const myLanguagePage = () => {createApiEndpoint(ENDPOINTS.PROJECT).create(data).
    then(res => {
      console.log(res);
    }).
    catch(err => console.log(err) )}

    return (
        <div className = {styles.main}>
            <Header/>
            <div className={styles.mid}>
                <div>
                    <h1>Setup Languages</h1>
                    <br/>
                    <p>Default locale
                    Set the default locale for this project.</p>
                    <br/>
                    <form>
                        <select onChange ={t => setSourceLanguage(t.target.value)} className= {styles.form1} id = "dropdown">
                            <option value="English">English-en</option>
                            <option value="Urdu">Urdu-ur</option>
                        </select>
 
                        <input onChange ={t => setsName(t.target.value)}
                        className= {styles.form1}  type="text" placeholder='Enter Name'/>
                    </form>
                </div>
 
                <div>
                <br/>
                <br/>
                <p>Languages
                Set the languages you want to translate to</p>
                <br/>
                <form>
                    <select onChange ={t => setTargetLanguage(t.target.value)} className= {styles.form1}  id = "dropdown">
                        <option value="English">English-en</option>
                        <option value="Urdu">Urdu-ur</option>
                    </select>
 
                    <input onChange ={t => settName(t.target.value)} className= {styles.form1}  type="text" placeholder='Enter Name'/>
                </form>
                </div>
                <button onClick={myLanguagePage} className={styles.button}><Link to='/home' style={{ color: 'inherit', textDecoration: 'inherit'}}>Submit</Link></button>
            </div>
        </div>
    )
}
