import React, { useContext } from "react";
import {
  BoldLink,
  BoxContainer,
  FormContainer,
  Input,
  MutedLink,
  SubmitButton,
} from "./common";
import { Marginer } from "../marginer";
import { AccountContext } from "./accountContext";
import { useState } from "react";
import { createApiEndpoint } from "../../api";

export function SignupForm(props) {
  const { switchToSignin } = useContext(AccountContext);
  const [FirstName, setFirstName] = useState(" ");
  const [LastName, setLastName] = useState(" ");
  const [Password, setPassword] = useState(" ");
  const [email, setEmail] = useState(" ");
  const [role, setRole] = useState(" ");


  const mySignUp = () => {createApiEndpoint("Register").create({FirstName:FirstName, lastName:LastName, Email:email, Password:Password, Role:role}).then(res => console.log(res)).catch(err => console.log(err) )}

  return (
    <BoxContainer>
      <FormContainer>
        <Input onChange ={(t) => setFirstName(t)}  type="text" placeholder="First Name" />
        <Marginer direction="vertical" margin="0.6em" />
        <Input onChange ={(t) => setLastName(t)}  type="text" placeholder="Last Name" />
        <Marginer direction="vertical" margin="0.6em" />
        <Input onChange ={(t) => setEmail(t)} type="email" placeholder="Email" />
        <Marginer direction="vertical" margin="0.6em" />
        <Input onChange ={(t) => setPassword(t)} type="password" placeholder="Password" />
        <Marginer direction="vertical" margin="0.6em" />
        <Input onChange ={(t) => setRole(t)} type="text" placeholder="Role" />
        <Marginer direction="vertical" margin="0.6em" />
      </FormContainer>
      <Marginer direction="vertical" margin={10} />
      <SubmitButton onClick={mySignUp} type="submit">Signup</SubmitButton>
      <Marginer direction="vertical" margin="1em" />
      <MutedLink href="#">
        Already have an account?
        <BoldLink href="#" onClick={switchToSignin}>
          Signin
        </BoldLink>
      </MutedLink>
    </BoxContainer>
  );
}
