import React from 'react';
import Header from '../header/header';
import Box from '@mui/material/Box';
import {
    FormContainer,
    SubmitButton
  } from "./common";
import { Marginer } from "../marginer";
import styles from './keyPage.module.css';
import { useState } from "react";
import { createApiEndpoint, ENDPOINTS } from "../../api";
 
export default function Keypage() {

    const [AllData, setAllData]= useState([]);
    const mydata = {SubDirectory:'FileStorage'};
    const [selectedItem, setSelectedItem] = useState({});
    const [selectedValue, setSelectedValue]= useState('')

    const callDisplayKey = () => {createApiEndpoint(ENDPOINTS.TRANSLATION).fetchAll(mydata).
        then(myresult => {console.log(myresult)
          console.log(myresult.data);
          setAllData(myresult.data); 
          }).
        catch(
          err => {
            console.log(err);
          }
         )}

    const [key, setKey] = useState('');
    const [value, setValue] = useState('');
    const data = {Key:key, Value:value};
  const saveKey = (data) => {
    const {id, key, value} = data;
    var obj = {KeyID:id , Key:key, Value:selectedValue}
    console.log(obj);
    createApiEndpoint(ENDPOINTS.TRANSLATION).update(obj.KeyID, obj).
  then(result => {console.log(result)
    console.log(result.data);
    }).
  catch(
    err => {
      console.log(err);
    }
   )
  }
  const SelectItem = (id, key, value) => {
     setSelectedValue(value)
     setSelectedItem({id, key, value})
  }
    return (
        <div className = {styles.main}>
            <Header/>
            <div className={styles.topContainer}>
                <div className= {styles.midContainer}>
                    <span className={styles.left}>
                    <SubmitButton onClick={callDisplayKey} type="submit">Show Data</SubmitButton>
                        
                        {/* <ol> */}
                        <table className= {styles.table} >
                          <tr className={styles.element}>
                            <th>index</th>
                            <th>key</th>
                            {/* <th>value</th> */}
                          </tr>
                            {AllData.map(({keyID, key , value}, index) => (
                              <tr key={keyID} onClick={()=> {SelectItem(keyID, key, value)}} className={styles.element}> 
                               
                                <td>{index+1}</td>
                                <td>{key}</td>
                                {/* <td className={styles.element}>{value}</td> */}
                              </tr>
                        ))}
                          </table>
                        {/* </ol> */}
                    </span>
 
                    <span className={styles.right}>
                        <div className={styles.rightTop}>
                            {/* <input value={selectedItem?.key || ''}disabled className = {styles.textBox} onChange ={t => setKey(t.target.value)} type="text" placeholder="Key" /> */}
                           
                        </div>
 
                        <div className={styles.rightbottom}>
                        <FormContainer onSubmit={(e)=> e.preventDefault()}>
                        {/* <select className= {styles.form1} id = "dropdown">
                                    <option value="en">en</option>
                                    <option value="ur">ur</option>
                        </select> */}
                        <input value={selectedItem?.key || ''}disabled className = {styles.textBox} onChange ={t => setKey(t.target.value)} type="text" placeholder="Key" />
                        <Marginer direction="vertical" margin="0.6em" />
                        <input value={selectedValue} className = {styles.textBox} onChange ={t => {
                        setSelectedValue(t.target.value);
                      }} type="text" placeholder="Value" />
                        <Marginer direction="vertical" margin="1em" />
                        <button classname={styles.saveButton} onClick={() => saveKey(selectedItem)} type="submit">Save</button>
                        <Marginer direction="vertical" margin="1em" />
                        </FormContainer>
                        
                        </div>
                    </span>
                </div>
            </div>
        </div>
    )
}
