import React, { useContext } from "react";
import {
  BoldLink,
  BoxContainer,
  FormContainer,
  Input,
  MutedLink,
  SubmitButton,
} from "./common";
import { Marginer } from "../marginer";
import { AccountContext } from "./accountContext";
import { useState } from "react";
import { createApiEndpoint, ENDPOINTS } from "../../api";

export function SignupForm(props) {
  const {switchToSignin } = useContext(AccountContext);
  const [fullName, setFullName] = useState('');
  const [userName, setUserName] = useState('');
  const [password, setPassword] = useState('');
  const [email, setEmail] = useState('');

  const data = {UserName:userName, Email:email, Password:password, FullName:fullName};
  const mySignUp = () => {createApiEndpoint(ENDPOINTS.REGISTER).create(data).
  then(res => console.log(res)).
  catch(err => console.log(err) )}

  // const validateForm = () => {
  //   let temp = {};
  //   temp.email = values.email != ""

  // }
  // const submitRequest = e => {
  //   e.preventDefault();
  //   if(validateForm()){

  //   }
  // }

  return (
    <BoxContainer>
      <FormContainer onSubmit={submitRequest}>
        <Input onChange ={t => setFullName(t.target.value)}  type="text" placeholder="Full Name" />
        <Marginer direction="vertical" margin="0.6em" />
        <Input onChange ={t => setUserName(t.target.value)}  type="text" placeholder="User Name" />
        <Marginer direction="vertical" margin="0.6em" />
        <Input onChange ={t => setEmail(t.target.value)} type="text" placeholder="Email" />
        <Marginer direction="vertical" margin="0.6em" />
        <Input onChange ={t => setPassword(t.target.value)} type="text" placeholder="Password" />
        <Marginer direction="vertical" margin="0.6em" />
      </FormContainer>
      <Marginer direction="vertical" margin={10} />
      <SubmitButton onClick={mySignUp} type="submit">Signup</SubmitButton>
      <Marginer direction="vertical" margin="1em" />
      <MutedLink href="#">
        Already have an account?
        <BoldLink href="#" onClick={switchToSignin}>
          Signin
        </BoldLink>
      </MutedLink>
    </BoxContainer>
  );
}
