import React from 'react';
import {Link} from 'react-router-dom';
import Header from '../header/header';
import { useState } from "react";
import { createApiEndpoint, ENDPOINTS } from "../../api";
import axios from 'axios';
 
 
import styles from './UploadFiles.module.css';
 
 export default function UploadFiles() {
    const [file, setFile] = useState();

    const onChangeFile = e => {
        // console.log('picture: ', picture);
        setFile(...file, e.target.files[0]);
    };       

    const data = {formFiles:file, subDirectory:'FileStorage'};
    const myFilePage = () => {createApiEndpoint(ENDPOINTS.FILEUPLOAD).create(data).
            then(res => {
              console.log(res);
            }).
            catch(err => console.log(err) )};
            
    return (
        <div className = {styles.main}>
            <Header/>
            <div className={styles.mid}>
                <div>
                    <h1>Upload File</h1>
                    <br/>
                    <p>File & Settings</p>
                    <br/>
                    <br/>
                    <form>
                        <p>File</p>
                        <input type="file" onChange = {e => onChangeFile(e)}/> 
                        <br/>
                        <p>Select the file you want to upload. We support all common file formats.</p>
                        <br/>
                        <p>Format</p>
                        <select className= {styles.form1}  id = "dropdown">
                            <option value="1">ARB (.arb)</option>
                            <option value="2">Ruby (.yml)</option>
                            <option value="3">Gettext (.po)</option>
                            <option value="4">.xml</option>
                            <option value="5">Simple JSON (.json)</option>
                        </select>
                    </form>
                </div>
                <button onClick={myFilePage} className={styles.button}><Link to='/home/keyPage' style={{ color: 'inherit', textDecoration: 'inherit'}}>Upload</Link></button>
            </div>
        </div>
    )
}
