                    <span className={styles.left}>

                    </span>
 
                    <span className={styles.right}>
                        <div className={styles.rightTop}>
                        <SubmitButton onClick={callDisplayKey} type="submit">Show Data</SubmitButton>
                        
                        <ol>
                        <table>
                          <tr>
                            <th>index</th>
                            <th>key</th>
                            <th>value</th>
                          </tr>
                            {AllData.map(({keyID, key , value}) => (
                              <tr onClick={()=> {SelectItem(keyID, key, value)}}> 
                                <td>{keyID}</td>
                                <td>{key}</td>
                                <td>{value}</td>
                              </tr>
                        ))}
                          </table>
                        </ol>
                      
                        </div>
 
                        <div className={styles.rightbottom}>
                        <FormContainer>
                        <select className= {styles.form1} id = "dropdown">
                                    <option value="en">en</option>
                                    <option value="ur">ur</option>
                        </select>
                        <input value={selectedItem?.key}disabled className = {styles.textBox} onChange ={t => setKey(t.target.value)} type="text" placeholder="Key" />
