import React, { useContext } from "react";
import {
  BoldLink,
  BoxContainer,
  FormContainer,
  Input,
  MutedLink,
  SubmitButton,
} from "./common";
import { Marginer } from "../marginer";
import { AccountContext } from "./accountContext";
import { useState } from "react";
import { createApiEndpoint, ENDPOINTS } from "../../api";
import Swal from "sweetalert2";

export function SignupForm(props) {

  var toastMixin = Swal.mixin({
    toast: true,
    icon: 'success',
    title: 'General Title',
    animation: false,
    position: 'top-right',
    showConfirmButton: false,
    timer: 3000,
    timerProgressBar: true,
    didOpen: (toast) => {
      toast.addEventListener('mouseenter', Swal.stopTimer)
      toast.addEventListener('mouseleave', Swal.resumeTimer)
    }
  });



  const {switchToSignin } = useContext(AccountContext);
  const [fullName, setFullName] = useState('');
  const [userName, setUserName] = useState('');
  const [password, setPassword] = useState('');
  const [email, setEmail] = useState('');

  const data = {UserName:userName, Email:email, Password:password, FullName:fullName};
  const mySignUp = () => {createApiEndpoint(ENDPOINTS.REGISTER).create(data).
  then(res => {
    console.log(res);

    toastMixin.fire({
      animation: true,
      title: 'Signed Up Successfully'
      });

    setFullName("");
    setUsername("");
    SetEmail("");
    setPassword("");


  }).
  catch(err => console.log(err) )}



  return (
    <BoxContainer>
      <FormContainer>
        <Input onChange ={t => setFullName(t.target.value)}  type="text" placeholder="Full Name" />
        <Marginer direction="vertical" margin="0.6em" />
        <Input onChange ={t => setUserName(t.target.value)}  type="text" placeholder="User Name" />
        <Marginer direction="vertical" margin="0.6em" />
        <Input onChange ={t => setEmail(t.target.value)} type="text" placeholder="Email" />
        <Marginer direction="vertical" margin="0.6em" />
        <Input onChange ={t => setPassword(t.target.value)} type="password" placeholder="Password" />
        <Marginer direction="vertical" margin="0.6em" />
      </FormContainer>
      <Marginer direction="vertical" margin={10} />
      <SubmitButton onClick={mySignUp} type="submit">Signup</SubmitButton>
      <Marginer direction="vertical" margin="1em" />
      <MutedLink href="#">
        Already have an account?
        <BoldLink href="#" onClick={switchToSignin}>
          Signin
        </BoldLink>
      </MutedLink>
    </BoxContainer>
  );
}
