    const getDownloadLink = () => {createApiEndpoint(ENDPOINTS.DOWNLOAD).fetchAll().
        then(response => {

            const url = window.URL.createObjectURL(new Blob([response]),{type:'application/zip'})
            const link = document.createElement('a')
            link.href = url
            console.log(url)
            console.log(link)
            link.setAttribute('download','FinalFiles')
            document.body.appendChild(link)
            link.click()
            link.parentNode.removeChild(link);
          }).
        catch(
          err => {
            console.log(err);
          }
         )}

    return(
        <div className={styles.main}>
            <Header/>

            <div className = {styles.mid}>
                <div className = {styles.text}>Congrats! Your file has been successfully Translated.</div>
