import React from 'react';
import {Link} from 'react-router-dom';
import Header from '../header/header';
import { useState } from "react";
import { createApiEndpoint, ENDPOINTS } from "../../api";
import axios from 'axios';
 
 
import styles from './UploadFiles.module.css';
 
export default function UploadFiles() {
    state = {
 
        // Initially, no file is selected
        selectedFile: null
      };
      
      // On file select (from the pop up)
      onFileChange = event => {
      
        // Update the state
        this.setState({ selectedFile: event.target.files[0] });
      
      };
      
      // On file upload (click the upload button)
      onFileUpload = () => {
      
        // Create an object of formData
        const formData = new FormData();
      
        // Update the formData object
        formData.append(
          "myFile",
          this.state.selectedFile,
          this.state.selectedFile.name
        );
      
        // Details of the uploaded file
        console.log(this.state.selectedFile);
      
        // Request made to the backend api
        // Send formData object
        // const myLanguagePage = () => {createApiEndpoint(ENDPOINTS.PROJECT).create(data).
        //     then(res => {
        //       console.log(res);
        //     }).
        //     catch(res => {
        //         console.log(res);
        //     )};
      };

    
    return (
        <div className = {styles.main}>
            <Header/>
            <div className={styles.mid}>
                <div>
                    <h1>Upload File</h1>
                    <br/>
                    <p>File & Settings</p>
                    <br/>
                    <br/>
                    <form>
                        <p>File</p>
                        <input type="file" /> 
                        <br/>
                        <p>Select the file you want to upload. We support all common file formats.</p>
                        <br/>
                        <p>Format</p>
                        <select className= {styles.form1}  id = "dropdown">
                            <option value="1">ARB (.arb)</option>
                            <option value="2">Ruby (.yml)</option>
                            <option value="3">Gettext (.po)</option>
                            <option value="4">.xml</option>
                            <option value="5">Simple JSON (.json)</option>
                        </select>
                    </form>
                </div>
                <button className={styles.button}><Link to='/home/keyPage' style={{ color: 'inherit', textDecoration: 'inherit'}}>Upload</Link></button>
            </div>
        </div>
    )
}
