       //return <Redirect to="/home" />
    }       
    else if(result.status == 400) {
      alert('Invalid User'); 
    }
    
    }).
  catch(err => console.log(err)
   )}
  
  return(
    <BoxContainer>
      <FormContainer>
        <Input onChange ={t => setUserName(t.target.value)} type="text" placeholder="UserName" />
